import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table, Button, Container, Modal, ModalBody, ModalHeader, FormGroup, ModalFooter}from 'reactstrap'



const data = [
  { id: 1, Nombre: "juan diego", Apellidos: "naruto", Matricula: "20000001"},
  { id: 2, Nombre: "pepe", Apellidos: "obe punchman", Matricula: "20000002"},
  { id: 3, Nombre: "pepe chato", Apellidos: " mushucu tensei", Matricula: "20000003"},
  { id: 4, Nombre: "pablito", Apellidos: "padres de familia", Matricula: "20000004"},
  { id: 5, Nombre: "ven", Apellidos: "dargonball", Matricula: "20000005"},
]

class App extends React.Component{
    state={
      data: data,
      form:{
        id:'',
        Nombre:'',
        Apellidos:'',
        Matricula:'',
      },
      modalInsertar: false,
      modalEditar: false,
    }


    handleChannge=e=>
    this.setState({
      form:{
        ...this.state.form,
        [e.target.name]: e.target.value,

}
});

mostrarModalInsertar=()=>{
  this.setState({modalInsertar: true});
}

ocultarModalInsertar=()=>{
  this.setState({modalInsertar: false});
}

mostrarModalEditar=(Registro)=>{
  this.setState({modalEditar: true, form: Registro});
}

ocultarModalEditar=()=>{
  this.setState({modalEditar: false});
}
//para guardar el nuevo valor que se agregara y actualizar lista
insertar=()=>{
  var valorNuevo={...this.state.form};
  valorNuevo.id=this.state.data.length+1;
  var lista=this.state.data;
  lista.push(valorNuevo);
  this.setState({data: lista, modalInsertar: false});

}

//para editar el nuevo valor

editar=(dato)=>{
    var contador=0;
    var lista=this.state.data;
    lista.map((registro)=>{
      if(dato.id==registro.id){
        lista[contador].Nombre=dato.Nombre;
        lista[contador].Apellidos=dato.Apellidos;
        lista[contador].Matricula=dato.Matricula;
      }
      contador++;
    });
    this.setState({data: lista, modalEditar: false});

}


    render(){
  return (
  <>
      <Container>
      <br/>
      <Button color="success" onClick={()=>this.mostrarModalInsertar()}>Insertar nuevo personage</Button>
    <br/><br/>

      <Table >

        <thead><tr><th>Id</th>
        <th>Nombre</th>
        <th>Apellidos</th>
        <th>Matricula</th>
        <th>Acciones</th></tr></thead>
        <tbody>
          {this.state.data.map((elemanto)=>(
            <tr>
                <td>{elemanto.id}</td>
                <td>{elemanto.Nombre}</td>
                <td>{elemanto.Apellidos}</td>
                <td>{elemanto.Matricula}</td>


                <td><Button color="primary" onClick={()=>this.mostrarModalEditar(elemanto)}>Modificar</Button>{" "}
                <Button color="danger">Eliminar</Button></td>
                </tr>

          ))}

        </tbody>


      </Table >

      </Container>

      <Modal isOpen={this.state.modalInsertar}>
        <ModalHeader>
          <div>
            <h3>Ingresar Registro</h3>
          </div>
        </ModalHeader>

        <ModalBody>
          <FormGroup>
            <label>Id</label>
            <input class="form-cclassontrol" readOnly type='text' value={this.state.data.length+1}/> 
          </FormGroup>
        </ModalBody>

        <ModalBody>
          <FormGroup>
            <label>Nombre</label>
            <input class="form-control" name="Nombre" type='text' onChange={this.handleChannge}/> 
          </FormGroup>
        </ModalBody>

        <ModalBody>
          <FormGroup>
            <label>Apellidos</label>
            <input class="form-control" name='Apellidos' type='text'onChange={this.handleChannge}/> 
          </FormGroup>
        </ModalBody>

        <ModalBody>
          <FormGroup>
            <label>Matricula</label>
            <input class="form-control" name='Matricula' type='text'onChange={this.handleChannge}/> 
          </FormGroup>
        </ModalBody>

        <ModalFooter>
          <Button color= 'primary' onClick={()=>this.insertar()}>Insertar</Button>
          <Button color= 'danger'onClick={()=>this.ocultarModalInsertar()}>Canselar</Button>
        </ModalFooter>

      </Modal>

      <Modal isOpen={this.state.modalEditar}>
        <ModalHeader>
          <div>
            <h3>Editar Registro</h3>
          </div>
        </ModalHeader>

        <ModalBody>
          <FormGroup>
            <label>Id</label>
            <input class="form-control" readOnly type='text' value={this.state.form.id}/> 
          </FormGroup>
        </ModalBody>

        <ModalBody>
          <FormGroup>
            <label>Nombre</label>
            <input class="form-control" name="Nombre" type='text' onChange={this.handleChannge} value={this.state.form.Nombre}/> 
          </FormGroup>
        </ModalBody>

        <ModalBody>
          <FormGroup>
            <label>Apellidos</label>
            <input class="form-control" name='Apellidos' type='text'onChange={this.handleChannge} value={this.state.form.Apellidos}/> 
          </FormGroup>
        </ModalBody>

        <ModalBody>
          <FormGroup>
            <label>Matricula</label>
            <input class="form-control" name='Matricula' type='text'onChange={this.handleChannge} value={this.state.form.Matricula}/> 
          </FormGroup>
        </ModalBody>


        <ModalFooter>
          <Button color= 'primary'onClick={()=>this.editar(this.state.form)}>Editar</Button>
          <Button color= 'danger' onClick={()=>this.ocultarModalEditar()}>Canselar</Button>
        </ModalFooter>

      </Modal>
    </>

  );
}
}

  export default App;
