import React from 'react';
import "./color.css"
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table, Button, Container, Modal, ModalBody, ModalHeader, FormGroup, ModalFooter}from 'reactstrap'



const data =[
  { Nivel: 1, periodo: "juan diego", horas: "naruto", calificacion: "20000001",estatus: "aprobado"},
  { Nivel: 2, periodo: "pepe", horas: "obe punchman", calificacion: "20000002",estatus: "aprobado"},
  { Nivel: 3, periodo: "pepe chato", horas: " mushucu tensei", calificacion: "20000003",estatus: "aprobado"},
  { Nivel: 4, periodo: "pablito", horas: "padres de familia", calificacion: "20000004",estatus: "aprobado"},
  { Nivel: 5, periodo: "ven", horas: "dargonball", calificacion: "20000005",estatus: "aprobado"},
]




//////////////////// CLASE DE LOS COMPONENTES DE CADA UNO DE LOS REGISTROS////////////////////////////////////

class App extends React.Component{
    state={
      data: data,
      form:{
        Nivel:'',
        periodo:'',
        horas:'',
        calificacion:'',
        estatus:'',
      },
      modalInsertar: false,
      modalEditar: false,
    }
    /////////////SELECCION DE LOS DATOS A EVALUAR//////////////////////  

    handleChannge=e=>
    this.setState({
      form:{
        ...this.state.form,
        [e.target.name]: e.target.value,

}
});

////////////////////////////////////////////////////////////////////////////////////////////////////////////////



///////////////////////////////  FUNCIONES DE LOS MODALS DE AGREGAR Y EDITAR /////////////////////////////////////


mostrarModalInsertar=()=>{
  this.setState({modalInsertar: true});
}

ocultarModalInsertar=()=>{
  this.setState({modalInsertar: false});
}

mostrarModalEditar=(Registro)=>{
  this.setState({modalEditar: true, form: Registro});
}

ocultarModalEditar=()=>{
  this.setState({modalEditar: false});
}


//////////////////////////////////////////////////////////////////////////////////////////////////////////



//////////////  FUNCIONES LOS BOTONES/////////////////////


//para guardar el nuevo valor que se agregara y actualizar lista
insertar=()=>{
  var valorNuevo={...this.state.form};
  valorNuevo.Nivel=this.state.data.length+1;
  var lista=this.state.data;
  lista.push(valorNuevo);
  this.setState({data: lista, modalInsertar: false});

}

//para editar el nuevo valor

editar=(dato)=>{
    var contador=0;
    var lista=this.state.data;
    lista.map((registro)=>{
      if(dato.Nivel==registro.Nivel){
        lista[contador].periodo=dato.periodo;
        lista[contador].horas=dato.horas;
        lista[contador].calificacion=dato.calificacion;
        lista[contador].estatus=dato.estatus;
      }
      contador++;
    });
    this.setState({data: lista, modalEditar: false});

}
//en esta funcion se podra eliminar los datos ingresado
eliminar=(dato)=>{
  var opcion=window.confirm("Desea Eliminar estos datos"+dato.Nivel);
  if (opcion){
    var contador=0;
    var lista = this.state.data;
    lista.map((Registro)=>{
      if(Registro.Nivel==dato.Nivel){
        lista.splice(contador, 1);
      }
        contador++;
    });
    this.setState({data:lista});
  }
}

